import {Link} from 'react-router-dom'
import Sidebar from './Sidebar'
import {useState, useEffect} from 'react'
import axios from 'axios'

const baseURL="http://127.0.0.1:8000/api"

function AddChapter() {

  const [chapterData, setChapterData]=useState({
    'title':'',
    'description': '',
    'f_img': '',
    'keywords': ''
  })

  function handleChange(event){
    setChapterData({
      ...chapterData,
      [event.target.name]: event.target.value
    })
  }

  function handleFileChange(event){
    setChapterData({
      ...chapterData,
      [event.target.name]: event.target.files[0]
    })
  }

  function handleSubmit(){
    const ChapterFormData = new FormData();
    ChapterFormData.append('teacher', 1);
    ChapterFormData.append('title', chapterData.title);
    ChapterFormData.append('description', chapterData.description);
    ChapterFormData.append('featured_img', chapterData.f_img, chapterData.f_img.name);
    ChapterFormData.append('keywords', chapterData.keywords);
    try {
      axios.post(baseURL+'/course/', ChapterFormData, {
        headers: {
          'content-type': 'multipart/form-data'
        }
      }).then((response)=>{
        setChapterData({
          'title':'',
          'description': '',
          'f_img': '',
          'keywords': ''
        });
      });
    }
    catch(error){
      console.log(error)
    }
  }

  return (
      <div className="container mt-4">
        <div className="row">
            <aside className="col-md-3">
              <Sidebar />
            </aside>
            <section className="col-md-9">
              <div className="card">
                <h5 className="card-header">Add Chapter</h5>
                  <div className="card-body">
                  <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Title</label>
                    <input type="text" class="form-control" id="exampleFormControlInput1" />
                  </div>
                  <div class="mb-3">
                    <label for="exampleFormControlTextarea1" class="form-label">Description</label>
                    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                  </div>
                  <div class="mb-3">
                    <label for="formFile" class="form-label">Video</label>
                    <input class="form-control" type="file" id="formFile" />
                  </div>
                  <div class="mb-3">
                    <label for="exampleFormControlTextarea1" class="form-label">Remarks</label>
                    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                  </div>
                  <hr />
                  <button className="btn btn-primary">Submit</button>
                </div>
              </div>
            </section>
        </div>
      </div>
    )
}

export default AddChapter;
