import {Link} from 'react-router-dom'
import {useEffect, useState} from 'react'
import axios from 'axios'

const baseURL='http://127.0.0.1:8000/api/teacher/';

function Register() {

  useEffect(() => {
    document.title='Teacher Register'
  })

  const [teacherData, setTeacherData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'qualification': '',
    'mobile_no': '',
    'skills': '',
    'status': ''
  })

  function handleChange(event){
    setTeacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    })
  }

  function handleSubmit(event){
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", teacherData.full_name)
    teacherFormData.append("email", teacherData.email)
    teacherFormData.append("password", teacherData.password)
    teacherFormData.append("qualification", teacherData.qualification)
    teacherFormData.append("mobile_no", teacherData.mobile_no)
    teacherFormData.append("skills", teacherData.skills)
    try {
      axios.post(baseURL, teacherFormData).then((response)=>{
        setTeacherData({
          'full_name': '',
          'email': '',
          'password': '',
          'qualification': '',
          'mobile_no': '',
          'skills': '',
          'status': 'success'
        });
      });
    }
    catch(error){
      console.log(error)
      setTeacherData({'status': 'error'})
    }
    }

    const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
    if (teacherLoginStatus == "true"){
      window.location.href='/teacher-dashboard'
    }
  return (

<div className="form-container">
  <form className="form--register" onSubmit={handleSubmit}>
      {teacherData.status == "success" && <p className='text-success'>Successfully Registered</p>}
      {!teacherData.status == "error" && <p className='text-danger'>Account did not register please try again.</p>}
      <div className="signup--welcome">
          <h1>Teacher Login</h1>
      </div>
      <div>
      <label className="form-label">Full Name</label>
      <input type="text"  placeholder="Full Name" className="form--input" name="full_name" onChange={handleChange} value={teacherData.full_name} />
      </div>
      <input
          type="email"
          placeholder="Email address"
          className="form--input"
          name="email"
          onChange={handleChange}
          value={teacherData.email}
      />
      <input
          type="password"
          placeholder="Password"
          className="form--input"
          name="password"
          onChange={handleChange}
          value={teacherData.password}
      />
      <input
          type="text"
          placeholder="Qualifications"
          className="form--input"
          name="qualification"
          onChange={handleChange}
          value={teacherData.qualification}
      />
      <input
          type="number"
          placeholder="Mobile No."
          className="form--input"
          name="qualification"
          onChange={handleChange}
          value={teacherData.mobile_no}
      />
      <textarea
          placeholder="Skills"
          className="form--input"
          name="skills"
          onChange={handleChange}
          value={teacherData.skills}
      ></textarea>
      <div id="emailHelp" class="form-text">Math, economics, chemistry, etc.</div>

      <button className="form--submit">Sign up</button>
  </form>
</div>
    )
}

export default Register;
