import {Link} from 'react-router-dom'
import {useEffect, useState} from 'react'
import axios from 'axios'
const baseURL='http://127.0.0.1:8000/api'

function Login() {
  useEffect(()=>{
    document.title="Teacher Login"
  })

  const [teacherLoginData, setTeacherLoginData]=useState ({
    'email': '',
    'password': ''
  })

  const [errorMsg,setErrorMsg]=useState('');

  function handleChange(event){
    setTeacherLoginData({
      ...teacherLoginData,
      [event.target.name] : event.target.value
    })
  }

  function handleSubmit(event){
    const teacherFormData = new FormData();
    teacherFormData.append('email', teacherLoginData.email)
    teacherFormData.append('password', teacherLoginData.password)
    try {
      axios.post(baseURL + '/teacher-login/', teacherFormData).then((response)=>
      {console.log(response.data)
        if(response.data.bool == true){
        localStorage.setItem("teacherLoginStatus", true)
        localStorage.setItem("teacherId", response.data.teacherId)
        window.location.href='/teacher-dashboard'
      }}
    )
    }
    catch(error){
      setErrorMsg("Invalid Email or Password")
    }
  }

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if (teacherLoginStatus == "true"){
    window.location.href='/teacher-dashboard'
  }

  return (
      <div className="container mt-4">
        <div className="row">
          <div className="col-6 offset-3">
            <div className="card">
              <h5 className="card-header">Teacher Login</h5>
              <div className="card-body">
              {errorMsg && <p className="text-danger">{errorMsg}</p>}
                <form>
                  <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Email address</label>
                    <input onChange={handleChange} name="email" value={teacherLoginData.email} type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Password</label>
                    <input onChange={handleChange} name="password" value={teacherLoginData.password} type="password" className="form-control" id="exampleInputPassword1" />
                  </div>
                  <div className="mb-3 form-check">
                    <input type="checkbox" className="form-check-input" id="exampleCheck1" />
                    <label className="form-check-label" for="exampleCheck1">Remember Me</label>
                  </div>
                  <button onClick={handleSubmit} type="button" className="btn btn-primary">Login</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Login;
