import {Link} from 'react-router-dom'
import {useEffect, useState} from 'react'
import axios from 'axios'

const baseURL='http://127.0.0.1:8000/api/teacher/';

function Register() {

  useEffect(() => {
    document.title='Teacher Register'
  })

  const [teacherData, setTeacherData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'qualification': '',
    'mobile_no': '',
    'skills': '',
    'status': ''
  })

  function handleChange(event){
    setTeacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    })
  }

  function handleSubmit(event){
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", teacherData.full_name)
    teacherFormData.append("email", teacherData.email)
    teacherFormData.append("password", teacherData.password)
    teacherFormData.append("qualification", teacherData.qualification)
    teacherFormData.append("mobile_no", teacherData.mobile_no)
    teacherFormData.append("skills", teacherData.skills)
    try {
      axios.post(baseURL, teacherFormData).then((response)=>{
        setTeacherData({
          'full_name': '',
          'email': '',
          'password': '',
          'qualification': '',
          'mobile_no': '',
          'skills': '',
          'status': true
        });
      });
    }
    catch(error){
      console.log(error)
      setTeacherData({'status':true})
    }
    }
  return (
      <div className="container mt-4">
        <div className="row">
          <div className="col-6 offset-3">
            {teacherData.status && <p className='text-success'>Successfully Registered</p>}
            <div className="card">
              <h5 className="card-header">Teacher Register</h5>
              <div className="card-body">
                {/* <form> */}
                  <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Full Name</label>
                    <input onChange={handleChange} value={teacherData.full_name} name="full_name" type="text" className="form-control"  />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Email</label>
                    <input onChange={handleChange} value={teacherData.email} name="email" type="email" className="form-control"  />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Password</label>
                    <input onChange={handleChange} value={teacherData.password} name="password" type="password" className="form-control" />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Qualification</label>
                    <input onChange={handleChange} value={teacherData.qualification} name="qualification" type="text" className="form-control" />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputEmail1" className="form-label">Mobile Number</label>
                    <input onChange={handleChange} value={teacherData.mobile_no} name="mobile_no" type="text" className="form-control" />
                  </div>
                  <div className="mb-3">
                    <label for="exampleInputPassword1" className="form-label">Skills</label>
                    <textarea onChange={handleChange} value={teacherData.skills} name="skills" className="form-control"></textarea>
                    <div id="emailHelp" class="form-text">Math, economics, chemistry, etc.</div>
                  </div>
                  <button onClick={handleSubmit} type="submit" className="btn btn-primary">Register</button>
                {/* </form> */}
              </div>
            </div>
          </div>
        </div>
      </div>
    )
}

export default Register;
