import {useParams} from 'react-router-dom'
import {Link} from 'react-router-dom'
import {useState, useEffect} from 'react'
import axios from 'axios'
const baseURL="http://127.0.0.1:8000/api"

function TeacherDetail(){
  const [courseData, setCourseData] = useState([]);
  const [teacherData, setTeacherData] = useState([]);
  const {teacher_id}=useParams()
  useEffect(()=>{
    try{
      axios.get(baseURL + '/teacher/' + teacher_id).then(response=>{
        setTeacherData(response.data)
      })
    }
    catch(error){
      console.log(error)
    }
  }, []);
  return (
    <div className="container mt-3">
      <div className="row">
        <div className="col-4">
          <img src="/Passport_photo.jpg" className="img-thumbnail" alt="Course image" />
        </div>
        <div className="col-8">
          <h3>John Doe</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Fusce faucibus lectus lectus, ac malesuada tellus blandit non. Nam vestibulum odio nisi, in tincidunt purus finibus id. Morbi iaculis tempus nisl, at pellentesque turpis. Vestibulum magna turpis, sagittis vel feugiat et, rutrum ac nibh. Vivamus non interdum eros. Fusce ut fermentum mauris, quis dictum elit. Aliquam rhoncus interdum diam non lobortis. Integer sit amet purus nec ex molestie accumsan. Integer tincidunt neque sollicitudin ligula pulvinar, vitae dignissim neque rhoncus. Donec mollis consequat arcu, a aliquam enim tincidunt vel. Quisque efficitur nisi lacus, non eleifend sapien condimentum id. Donec nec aliquet tortor, vel tincidunt ante. Aenean finibus eu nisl nec ullamcorper. Proin in sapien eget leo  euismod mattis sit amet ullamcorper libero. Sed diam est, mattis eu mauris quis, fringilla mattis leo.</p>
          <p><b>Recent Courses: </b> <Link to="/detail/1">Learn React</Link></p>
          <p><b>Rating: </b> 4.5/5</p>
        </div>
      </div>
      {/* Course Videos */}
      <div className="card mt-4">
        <div className="card-header">
          <h5>Course List</h5>
        </div>
        <div className="list-group list-group-flush">
          <Link to='/detail/1' class='list-group-item list-group-item-action'>Learn React</Link>
          <Link to='/detail/1' class='list-group-item list-group-item-action'>Learn Python</Link>
          <Link to='/detail/1' class='list-group-item list-group-item-action'>Learn Discrete Maths</Link>
        </div>
      </div>
    </div>
  );
}

export default TeacherDetail;
