import {useParams} from 'react-router-dom'
import {Link} from 'react-router-dom'
import {useState, useEffect} from 'react'
import axios from 'axios'
const baseURL="http://127.0.0.1:8000/api"

function CourseDetail(){
  const [chapterData, setChapterData] = useState([]);
  const [courseData, setCourseData] = useState([]);
  const [teacherData, setTeacherData] = useState([]);
  const {course_id}=useParams()
  // Fetch course details on page load
  useEffect(()=>{
    try{
      axios.get(baseURL + '/course/' + course_id).then(response=>{
        setCourseData(response.data)
        setTeacherData(response.data.teacher)
        setChapterData(response.data.course_chapters)
      })
    }
    catch(error){
      console.log(error)
    }
  }, []);
  // console.log(courseData)
  // console.log(teacherData)
  // console.log(chapterData)
  return(
    <div className="container mt-3">
      <div className="row">
        <div className="col-4">
          <img src={courseData.featured_img} className="img-thumbnail" alt="Course image" />
        </div>
        <div className="col-8">
          <h3>{courseData.title}</h3>
          <p>{courseData.description}</p>
          <p><b>Course By: </b> <Link to="/teacher-detail/1">{teacherData.full_name}</Link></p>
          <p><b>Duration: </b> 3 Hours 30 Mins</p>
          <p><b>Total Enrolled: </b> 100</p>
          <p><b>Rating: </b> 4/5</p>
        </div>
      </div>
      {/* Course Videos */}
      <div className="card mt-4">
        <div className="card-header">
          <h5>Course Videos</h5>
        </div>
        <ul className="list-group list-group-flush">
        {chapterData.map((chapter,index)=>
          <li className="list-group-item">{chapter.title}
            <span className="float-end">
              <span className="me-3">5 Min 30 Sec</span>
              <button className="btn btn-sm" data-bs-toggle="modal" data-bs-target="#videoModal1"><i className="bi bi-play-btn"></i></button>
            </span>
            {/* Video Modal Start */}
            <div className="modal fade" id="videoModal1" tabindex="-1" aria-labelledby="videoModalLabel" aria-hidden="true">
              <div className="modal-dialog modal-xl">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title" id="videoModalLabel">Video 1</h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                  </div>
                  <div className="modal-body">
                    <div className="ratio ratio-16x9">
                      <iframe src={chapter.video} title={chapter.title} allowfullscreen></iframe>
                    </div>
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                  </div>
                </div>
              </div>
            </div>
            {/* Video Modal End */}
          </li>
        )}
        </ul>
      </div>
    </div>
  )
}

export default CourseDetail
