import {Link} from 'react-router-dom'
import {useEffect, useState} from 'react'
import axios from 'axios'

const baseURL='http://127.0.0.1:8000/api/teacher/';

function Register() {

  useEffect(() => {
    document.title='Teacher Register'
  })

  const [teacherData, setTeacherData] = useState({
    'full_name': '',
    'email': '',
    'password': '',
    'qualification': '',
    'mobile_no': '',
    'skills': '',
    'status': ''
  })

  function handleChange(event){
    setTeacherData({
      ...teacherData,
      [event.target.name]: event.target.value
    })
  }

  function handleSubmit(event){
    const teacherFormData = new FormData();
    teacherFormData.append("full_name", teacherData.full_name)
    teacherFormData.append("email", teacherData.email)
    teacherFormData.append("password", teacherData.password)
    teacherFormData.append("qualification", teacherData.qualification)
    teacherFormData.append("mobile_no", teacherData.mobile_no)
    teacherFormData.append("skills", teacherData.skills)
    try {
      axios.post(baseURL, teacherFormData).then((response)=>{
        setTeacherData({
          'full_name': '',
          'email': '',
          'password': '',
          'qualification': '',
          'mobile_no': '',
          'skills': '',
          'status': 'success'
        });
      });
    }
    catch(error){
      console.log(error)
      setTeacherData({'status': 'error'})
    }
    }

    const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
    if (teacherLoginStatus == "true"){
      window.location.href='/teacher-dashboard'
    }
  return (
    <div className="form-container">
        <form className="form--register" >
            {teacherData.status == "success" && <p className='text-success'>Successfully Registered</p>}
            {!teacherData.status == "error" && <p className='text-danger'>Account did not register please try again.</p>}
            <div className="register--welcome">
                <h1>Teacher Register</h1>
            </div>
            <div className="form--div">
              <label className="form-label">Full Name</label>
              <input type="text" className="form--input" name="full_name" onChange={handleChange} value={teacherData.full_name} />
            </div>
            <div className="form--div">
              <label className="form-label">Email</label>
              <input type="email" className="form--input" name="email" onChange={handleChange} value={teacherData.email} />
            </div>
            <div className="form--div">
              <label className="form-label">Password</label>
              <input type="password" className="form--input" name="password" onChange={handleChange} value={teacherData.password} />
            </div>
            <div className="form--div">
              <label className="form-label">Qualifications</label>
              <input type="text" className="form--input" name="qualification" onChange={handleChange} value={teacherData.qualification} />
            </div>
            <div className="form--div">
              <label className="form-label">Mobile Number</label>
              <input type="number" className="form--input" name="qualification" onChange={handleChange} value={teacherData.mobile_no} />
            </div>
            <div className="form--div">
              <label className="form-label">Skills</label>
              <textarea className="form--input" name="skills" onChange={handleChange} value={teacherData.skills}></textarea>
            <div id="emailHelp" class="form-text">Math, economics, chemistry, etc.</div>
            </div>


            <button className="form--submit">Sign up</button>
        </form>
    </div>
    )
}

export default Register;
