import {Link} from 'react-router-dom'
import {useState, useEffect} from 'react'
import axios from 'axios'
import CourseCard from "./CourseCard"
const baseURL="http://127.0.0.1:8000/api"

function AllCourses() {
  const [courseData, setCourseData] = useState([]);
  // Fetch courses when page load
  useEffect(()=>{
    try{
      axios.get(baseURL + '/course/').then(response=>{
        setCourseData(response.data)
      })
    }
    catch(error){
      console.log(error)
    }
  }, []);

  const cards = courseData.map(item => {
        return (
            <CourseCard
                key={item.id}
                item={item}
            />
        )
    })

  return (
    <div className="container mt-4">
      <h3 className="pb-1 mb-4">Latest Courses</h3>
      <div className="row">
        {courseData && courseData.map((course, index) =>
        <div className="col-md-3 mb-4">
          <div className="card">
            <Link to={`/detail/${course.id}`}> <img src={course.featured_img} className="card-img-top" alt={course.title} /></Link>
            <div className="card-body">
              <h5 className="card-title"><Link to={`/detail/${course.id}`}>{course.title}</Link></h5>
            </div>
          </div>
        </div>
      )}
      </div>
      <CourseCard />
      {/* Pagination */}
      <nav aria-label="Page navigation example mt-4">
        <ul className="pagination justify-content-center">
          <li className="page-item"><a className="page-link" href="#">Previous</a></li>
          <li className="page-item"><a className="page-link" href="#">1</a></li>
          <li className="page-item"><a className="page-link" href="#">2</a></li>
          <li className="page-item"><a className="page-link" href="#">3</a></li>
          <li className="page-item"><a className="page-link" href="#">Next</a></li>
        </ul>
      </nav>
    </div>
  )
}

export default AllCourses
